package com.javaex.practice;

public class Ex08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int x = 1; x <= 9; x++) {
			for (int i = 2; i <= 9; i++) {
				System.out.print(i + "*" + x + "=" + x * i+" ");
			}
			System.out.println("");
		}
	}

}
